import React, { useState, useEffect } from 'react';
import { sessionAPI, feedbackAPI } from '../../utils/api';
import SessionCard from '../SessionCard/SessionCard';
import FeedbackForm from '../FeedbackForm/FeedbackForm';
import './Dashboard.css';

const Dashboard = ({ user, onLogout }) => {
  const [sessions, setSessions] = useState([]);
  const [myFeedback, setMyFeedback] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedSession, setSelectedSession] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedTrack, setSelectedTrack] = useState('All');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      const [sessionsRes, feedbackRes, categoriesRes] = await Promise.all([
        sessionAPI.getSessions(),
        feedbackAPI.getMyFeedback(),
        feedbackAPI.getCategories()
      ]);
      setSessions(sessionsRes.data);
      setMyFeedback(feedbackRes.data);
      setCategories(categoriesRes.data);
    } catch (error) {
      setError('Failed to load data');
    } finally {
      setLoading(false);
    }
  };

  const handleFeedbackClick = (session) => {
    setSelectedSession(session);
    setSelectedCategory(null);
  };

  const handleCategoryFeedbackClick = (category) => {
    if (category.id === 'sessions') {
      // Scroll to sessions section
      document.querySelector('.sessions-section')?.scrollIntoView({ behavior: 'smooth' });
    } else {
      setSelectedCategory(category);
      setSelectedSession(null);
    }
  };

  const handleFeedbackSubmit = () => {
    loadData();
  };

  const hasFeedback = (sessionId) => {
    return myFeedback.some(f => f.category === 'session' && f.sessionId === sessionId);
  };

  const hasCategoryFeedback = (categoryId) => {
    return myFeedback.some(f => f.category === categoryId);
  };

  const getFilteredSessions = () => {
    if (selectedTrack === 'All') return sessions;
    if (selectedTrack === 'General') {
      return sessions.filter(s => !s.track || s.track === 'General' || s.track === '');
    }
    return sessions.filter(s => s.track === selectedTrack);
  };

  const getTracks = () => {
    const tracks = ['All', 'General'];
    const uniqueTracks = [...new Set(sessions.map(s => s.track).filter(t => t && t !== 'General'))];
    return [...tracks, ...uniqueTracks];
  };

  if (loading) {
    return <div className="loading">Loading sessions...</div>;
  }

  return (
    <div className="dashboard">
      <header className="header">
        <div className="container">
          <div className="header-content">
            <div>
              <h1>AWS UG Vadodara Community Day</h1>
              <p className="subtitle">Welcome, {user.name || user.email}</p>
            </div>
            <button className="btn btn-secondary" onClick={onLogout}>
              Logout
            </button>
          </div>
        </div>
      </header>

      <main className="container">
        {error && <div className="error-message">{error}</div>}

        <div className="dashboard-stats">
          <div className="stat-card">
            <h3>{sessions.length}</h3>
            <p>Total Sessions</p>
          </div>
          <div className="stat-card">
            <h3>{myFeedback.filter(f => f.category === 'session').length}</h3>
            <p>Session Feedback</p>
          </div>
          <div className="stat-card">
            <h3>{myFeedback.filter(f => f.category !== 'session').length}</h3>
            <p>Other Feedback</p>
          </div>
        </div>

        <div className="feedback-categories">
          <h2>Event Feedback</h2>
          <div className="categories-grid">
            {categories.map((category) => (
              <div key={category.id} className="category-card">
                <h3>{category.name}</h3>
                <p>{category.description}</p>
                {category.id === 'sessions' ? (
                  <div>
                    <span className="session-count">{myFeedback.filter(f => f.category === 'session').length}/{sessions.length} Sessions Rated</span>
                    <button 
                      className="btn btn-primary"
                      onClick={() => handleCategoryFeedbackClick(category)}
                    >
                      View Sessions
                    </button>
                  </div>
                ) : hasCategoryFeedback(category.id) ? (
                  <span className="feedback-submitted">✓ Feedback Submitted</span>
                ) : (
                  <button 
                    className="btn btn-primary"
                    onClick={() => handleCategoryFeedbackClick(category)}
                  >
                    Give Feedback
                  </button>
                )}
              </div>
            ))}
          </div>
        </div>

        <div className="sessions-section">
          <div className="sessions-header">
            <h2>Sessions</h2>
          </div>

          <div className="track-filter-sticky">
            <div className="track-buttons">
              {getTracks().map(track => (
                <button
                  key={track}
                  className={`track-btn ${selectedTrack === track ? 'active' : ''}`}
                  onClick={() => setSelectedTrack(track)}
                >
                  {track}
                </button>
              ))}
            </div>
          </div>

          {getFilteredSessions().length === 0 ? (
            <p>No sessions available for {selectedTrack}</p>
          ) : (
            <div className="sessions-grid">
              {getFilteredSessions().map((session) => (
                <SessionCard
                  key={session.sessionId}
                  session={session}
                  onFeedbackClick={handleFeedbackClick}
                  hasFeedback={hasFeedback(session.sessionId)}
                />
              ))}
            </div>
          )}
        </div>
      </main>

      <footer className="footer">
        <div className="container">
          <div className="footer-content">
            <div className="footer-section">
              <h4>AWS UG Vadodara</h4>
              <p>Community Day 2025</p>
            </div>
            <div className="footer-section">
              <p>Powered by AWS Community</p>
              <p>© 2025 AWS User Group Vadodara</p>
            </div>
          </div>
        </div>
      </footer>

      {selectedSession && (
        <FeedbackForm
          session={selectedSession}
          category="session"
          onClose={() => setSelectedSession(null)}
          onSubmit={handleFeedbackSubmit}
        />
      )}

      {selectedCategory && (
        <FeedbackForm
          category={selectedCategory.id}
          categoryName={selectedCategory.name}
          onClose={() => setSelectedCategory(null)}
          onSubmit={handleFeedbackSubmit}
        />
      )}
    </div>
  );
};

export default Dashboard;
